import React, { Component } from 'react';
import DeleteBtn from '../components/DeleteBtn';
import Jumbotron from '../components/Jumbotron';
import API from '../utils/API';
import { Link } from 'react-router-dom';
import { Col, Row, Container } from '../components/Grid';
import { List, ListItem } from '../components/List';
import { Input, TextArea, FormBtn } from '../components/Form';

class Books extends Component {
	state = {
		services: [],
        offerer:"",
        email:"",
        descritpion: "",
        serviceType:""

	}

	componentDidMount() {
		this.loadgetServices();
	}

	loadServicess = () => {
		API.Services()
			.then((res) => this.setState({ services: res.data, offerer: '', email: '', descritpion: '' }))
			.catch((err) => console.log(err));
	};

	deleteServices = (id) => {
		API.deleteServices(id).then((res) => this.loadServices()).catch((err) => console.log(err));
	}

	handleInputChange = (event) => {
		const { name, value } = event.target;
		this.setState({
			[name]: value
		})
	}

	handleFormSubmit = (event) => {
		
			API.saveServices({
				name: this.state.offerer,
				email: this.state.email,
				description: this.state.description,
				serviceType: this.state.serviceType
			})
				.then((res) => this.loadServices())
				.catch((err) => console.log(err));
		
	}

	render() {
		return (
			<Container fluid>
				<Row>
					<Col size="md-4">
						<Jumbotron>
							<h1>Let Us Help You Connect</h1>
							<h4>
								Are you in need of that special addition to your party or event? Or do you have talents
								and services to make someone's day extra special? Please let fill out the form below so
								we can all be sucessful!{' '}
							</h4>
						</Jumbotron>
						<form>
							<Input
								value={this.state.name}
								onChange={this.handleInputChange}
								name="offerer"
								placeholder="Name (required)"
							/>
							<Input
								value={this.state.email}
								onChange={this.handleInputChange}
								name="email"
								placeholder="Email (required)"
							/>
             
							<label>
								<Input
									type="radio"
									name="serviceType"
									value="option1"
									onChange={this.handleInputChange}
								/>
                Seeking Event / Party Services
              
							</label>
              <br></br>
							<label>
								<Input
									type="radio"
									name="serviceType"
									value="option2"
									onChange={this.handleInputChange}
								/>
								Professional Party/Event Specialists for Hire
							</label>
							
							<TextArea
								value={this.state.synopsis}
								onChange={this.handleInputChange}
								name="description"
								placeholder="Tell Us About Your Event or Talents (Required)"
							/>
							<FormBtn disabled={!(this.state.offerer && this.state.email)} onClick={this.handleFormSubmit}>
								Submit
							</FormBtn>
						</form>
					</Col>
					<Col size="md-4 sm-6">
						<Jumbotron>
							<h1>Services Requested</h1>
						</Jumbotron>
						
							<List>
								{this.state.services.map((service) => 
								     (
									<ListItem key={service._id}>
										<Link to={'/connection/' + service._id}>
											<strong>
												{service.offerer}
												<br />
												{service.email}
												<br />
												{service.description}
											</strong>
										</Link>
										<DeleteBtn onClick={() => this.deleteServices(service._id)} />
									</ListItem>
								))
								
                                     }
                                </List>
						 
						
						
					</Col>
					<Col size="md-4 sm-6">
						<Jumbotron>
							<h1>Services Renderred</h1>
						</Jumbotron>
						{this.state.books.length ? (
							<List>
								{this.state.books.map((book) => (
									<ListItem key={book._id}>
										<Link to={'/books/' + book._id}>
											<strong>
												{book.name}
												<br />
												{book.email}
												<br />
												{book.synopsis}
											</strong>
										</Link>
										<DeleteBtn onClick={() => this.deleteBook(book._id)} />
									</ListItem>
								))}
							</List>
						) : (
							<h3>No Results to Display</h3>
						)}
					</Col>
				</Row>
			</Container>
		);
	}
}

export default Books;
